import { createSlice } from "@reduxjs/toolkit";


export const taskerSlice = createSlice({
    name:'taskStore',
    initialState:{
        tasks:[] 
    },
    reducers:{
        addTask:(state,action)=>{
            const newTask = action.payload;
            if(state.tasks.some(initTask=> initTask.title === newTask.title)){
                alert('Такое задание уже существует');
            }
            else{
                state.tasks.push(newTask);
            }
        },
        removeTask:(state,action)=>{
            const taskId = action.payload;
            state.tasks = state.tasks.filter(initTask=> initTask.id !== taskId);
        },
        changeDoneState:(state,action)=>{
            const taskId = action.payload;
            state.tasks = state.tasks.map(initTask => initTask.id === taskId ? {...initTask,done:!initTask.done}:initTask );
        }
    }
});

export const {addTask, removeTask, changeDoneState} = taskerSlice.actions;
export default taskerSlice.reducer;
