import { TaskFilter } from '../Constants/Constants';
import { makeObservable } from 'mobx';

const actions = {AddTask:'AddTask', RemoveTask:'RemoveTask', ToggleStatusTask:'ToggleStatusTask'}

class TaskStore{
    tasks = [];
    taskFilter = TaskFilter.All;

    constructor(tasks){
        makeAutoObservable(this);
    }

    GetTasks(){
        return this.tasks.filter(this.taskFilter.func);
    }

    AddTask(task){
        this.tasks.push(task);
    }

    RemoveTask(taskId){
        this.tasks = this.tasks.filter(task=>task.id !== taskId) 
    }

    ToogleStatusTask(taskId){
        this.tasks= this.tasks.map(initTask => initTask.id === taskId ? {...initTask,done:!initTask.done}:initTask );
    }

    changeFilter(filterName){
        this.taskFilter = TaskFilter.filterName
    }
}

export default  new TaskStore();
