import {Component} from "react";
import { TaskFilter } from "../../constants";
export class Selector extends Component{
    constructor(props){
        super(props);
        this.state={
            value:this.props.parentValue
        }
        this.changeOptionHandler = this.changeOptionHandler.bind(this);
    }

    changeOptionHandler(e){
        const indexOfSelectedOption =[...e.target.options].findIndex(option=>option.selected);
        this.setState({value:this.props.options[indexOfSelectedOption]})
        this.props.parentChangeValueHandler(this.state.value);
    }

    render(){
        
        const {title, parentValue,options} = this.props;

        return <label>{title}
                    <select onChange={this.changeOptionHandler} 
                            value={parentValue}>
                        {options.map(option=> <option {...option}></option>)}
                    </select>
                </label>
    }
}
