import {Component} from "react";
import uuid from "react-uuid";

class TaskFilterSelector extends Component{
    
    render() {

        const [parentChangeFilterHandler, parentFilterValue, options] = this.props;
        return (
            <label>Показать
                <select value={parentFilterValue}
                    onChange={parentChangeFilterHandler}>
                    {options.map(filter => <option {...option}></option>)}
                </select>
            </label>
        );
    }
}

export {TaskFilterSelector};
