import {Component} from "react";
import uuid from "react-uuid";

class TaskFilterSelector extends Component{
    
    render() {
        const options = [{key:uuid(),value:(task)=>task, label: 'Все'},
                         {key:uuid(),value:(task)=>(task.done===true),  label:'Выполненные'},
                         {key:uuid(),value:(task)=>(task.done===false),label:'Текущие'}];
        const [parentChangeFilterHandler, parentFilterValue] = this.props;
        return (
            <label>Показать
                <select value={parentFilterValue}
                    onChange={parentChangeFilterHandler}>
                    {options.map(filter => <option key ={filter.key}  
                                            value={filter.value.toString()} 
                                            label={filter.label}></option>)}
                </select>
            </label>
        );
    }
}

export {TaskFilterSelector};
