import { makeAutoObservable, makeObservable, observable, computed, action, reaction } from 'mobx';
import uuid from 'react-uuid';

class TaskStore {
  constructor() {
    this.todos = [];
    this.taskFilter = TaskFilter.All;
    makeObservable(this, {
      filteredTasks: observable,
      taskFilterName: observable,
      todos: observable,
      AddTask: action,
      RemoveTask: action,
      GetTasks: action,
      ChangeFilter: action
    });
    reaction(() => this.todos, _ => console.log(this.todos));
  }

  FilterTasks() {
    this.filteredTasks = this.todos.filter(this.TaskFilter.TaskFilterName.func);
  }

  AddTask(task) {
    this.todos = [...this.todos, { ...task,
      id: uuid()
    }];
    this.FilterTasks();
  }

  RemoveTask(taskId) {
    this.todos = this.todos.filter(task => task.id !== taskId);
    this.FilterTasks();
  }

  ToogleStatusTask(taskId) {
    this.todos = this.todos.map(initTask => initTask.id === taskId ? { ...initTask,
      done: !initTask.done
    } : initTask);
    this.FilterTasks();
  }

  ChangeFilter(filterName) {
    this.taskFilterName = filterName;
    this.FilterTasks();
  }

}

export default new TaskStore();

