import { createSlice } from "@reduxjs/toolkit";

const initialState = {tasks:[]};

export const taskerSlice = createSlice({
    name:'tasker',
    initialState,
    reducers:{
        addTask:(state,action)=>{
            if(state.tasks.some(initTask=> initTask.title === action.task.title)){
                alert('Такое задание уже существует');
            }
            else{
                state.tasks.push(action.task);
            }
        },
        removeTask:(state,action)=>{
            state.tasks = state.tasks.filter(initTask=> initTask.id !== action.task.id);
        }
    }
});

export const {addTask, removeTask} = taskerSlice.actions;
export default taskerSlice.reducer;
