import { createSlice } from "@reduxjs/toolkit";

const initialState = {tasker:[]};

export const taskerSlice = createSlice({
    name:'tasker',
    initialState,
    reducers:{
        addTask:(state,action)=>{
            if(state.tasker.some(initTask=> initTask.title === action.payload.title)){
                alert('Такое задание уже существует');
            }
            else{
                state.tasker.push(action.payload);
            }
        },
        removeTask:(state,action)=>{
            state.tasker = state.tasker.filter(initTask=> initTask.id !== action.pa.id);
        },
        changeDoneState:(state,action)=>{
            const taskId = action.payload;
            state.tasker = state.tasker.map(initTask => initTask.id === taskId ? {...initTask,done:!initTask.done}:initTask );
        }
    }
});

export const {addTask, removeTask, changeDoneState} = taskerSlice.actions;
export default taskerSlice.reducer;
