import './App.css';
import {TaskHolder} from './Components/TaskHolder/TaskHolder'
import {ThemeContext,theme} from './contexts/ThemeContext'
import { useState, useContext } from 'react';

function App(props) {
    const theme = useContext(ThemeContext);
    // const toggleThemeHandler = ()=>{
    // this.setState(prevState=>{return {
    //   theme:prevState.theme === theme.usual ? theme.dark: theme.usual}
    // });

   return (
     <ThemeContext.Provider value = {theme.usual}> 
      <div className="App">
          <button className={`${theme}ThemeBtn ThemeBtn`} 
            onClick={this.toggleThemeHandler}>
              {this.state.theme === theme.usual ? 'Темная тема': 'Светлая тема'}</button>
          <TaskHolder />
      </div>
      </ThemeContext.Provider>)
}


export default App;
