import { TaskFilter } from '../Constants/Constants';
import { makeAutoObservable,makeObservable,observable, computed, action } from 'mobx';

class TaskStore{
    tasks = []
    taskFilter = TaskFilter.All

    constructor(){
        makeObservable(this,{
            taskFilter:observable,
            tasks:observable,
            AddTask:action,
            RemoveTask:action,
            GetTasks:computed
        });
    }

    get GetTasks(){return this.tasks.filter(this.taskFilter.func);}

    AddTask(task){
        this.tasks= [...this.tasks,task];
    }

    RemoveTask(taskId){
        this.tasks = this.tasks.filter(task=>task.id !== taskId) 
    }

    ToogleStatusTask(taskId){
        this.tasks= this.tasks.map(initTask => initTask.id === taskId ? {...initTask,done:!initTask.done}:initTask );
    }
}

export default  new TaskStore();
