import {Component} from "react";
import { TaskBgColors } from "../../constants";
import './Task.css';

class Task extends Component{
    defineBackgroundColor(){
        switch (this.props.priority){
            case 'High':{
                return 'HighPriorityTask';
            }
            case 'Medium':{
                return 'MediumPriorityTask';
            }
            case 'Low':{
                return  'LowPriorityTask';
            }
            default: {
                return '';
            }
        }
    }

    render(){
        const {changeDoneTaskParentHandler, removeTaskParentHandler, priority} = this.props; 
        return <div className={`Task ${TaskBgColors[priority]}`}>
                    <div className='TaskHeader '>
                        <h1>{this.props.title}</h1>
                        <div className='TaskControls'>
                            <button onClick={()=>removeTaskParentHandler(this.props.id)}>Удалить</button>
                        </div>
                    </div>
                    <div className='CompleteBlock'>
                        <label htmlFor='complete'>Выполнено:</label>
                        <input id='complete' 
                               type='checkbox' 
                               checked={this.props.done}
                               onChange={()=>changeDoneTaskParentHandler(this.props.id)}/>
                    </div>
              </div>
    }
}

export {Task}
