import uuid from "react-uuid";

export const Filter = [{key: uuid(),value:'All', func:(task)=>task, label: 'Все' },
             {key: uuid(),value:'Completed', func:(task)=>(task.done===true),  label:'Выполненные'},
             {key: uuid(), value:'NotComplited', func:(task)=>(task.done===false),label:'Текущие'}];

export const Priority =[{key: uuid(),value:'Low',label:'Низкий'},
                {key: uuid(),value:'Medium', label:'Средний'},
                {key: uuid(),value:'High',label:'Высокий'}];

export const bgColors = {High: 'HighPriorityTask', Medium:'Medium', Low:'Low'};
