import './App.css';
import {TaskHolder} from './Components/TaskHolder/TaskHolder'
import {ThemeContext,theme} from './contexts/ThemeContext'
import { useState, useContext } from 'react';

export default function App(props) {
    const toggleThemeHandler = ()=>{setTheme(theme === theme.usual ? theme.dark: theme.usual);}
    const [theme,setTheme] = useState(theme.usual);

   return (
    <ThemeContext.Provider value = {theme}> 
      <div className="App">
          <button className={`${theme}ThemeBtn ThemeBtn`} 
            onClick={toggleThemeHandler}>
              {theme === theme.usual ? 'Темная тема': 'Светлая тема'}</button>
          <TaskHolder />
      </div>
    </ThemeContext.Provider>)
}
