import './App.css';
import {TaskHolder} from './Components/TaskHolder/TaskHolder'
import React from 'react'
import {ThemeContext,theme} from './contexts/ThemeContext'
class App extends React.Component {

  constructor(props){
    super(props);
    this.state={
      theme:this.context
    };

    this.toggleThemeHandler = this.toggleThemeHandler.bind(this);
  }

  toggleThemeHandler(){
    if(this.context === theme.usual){
      this.context = theme.dark;
    }else{
      this.context = theme.usual;
    }
  }

  render(){ return ( 
      <div className="App">
          <button onClick={this.toggleThemeHandler}></button>
          <TaskHolder />
      </div>)
  }
}

export default App;
