import { TaskFilter } from '../Constants/Constants';
import { makeAutoObservable,makeObservable,observable, computed, action, reaction } from 'mobx';
import uuid from 'react-uuid';

class TaskStore{
    todos = [{id:uuid(),title:'asd',done:false}]
    taskFilter = TaskFilter.All

    constructor(){
        makeObservable(this,{
            taskFilter:observable,
            todos:observable,
            AddTask:action,
            RemoveTask:action,
            GetTasks:computed
        });
        reaction(()=>this.todos,_=>console.log(this.todos));
    }

    get GetTasks(){return this.todos.filter(this.taskFilter.func);}

    AddTask(task){
        this.todos= [...this.todos,task];
    }

    RemoveTask(taskId){
        console.log(this);
        this.todos = this.todos.filter(task=>task.id !== taskId) 
    }

    ToogleStatusTask(taskId){
        this.todos= this.todos.map(initTask => initTask.id === taskId ? {...initTask,done:!initTask.done}:initTask );
    }
}

export default  new TaskStore();
