import uuid from "react-uuid";

export const Filter = {All:{key: uuid(),value:(task)=>task, label: 'Все'},
            Complited: {key: uuid(),value:(task)=>(task.done===true),  label:'Выполненные'},
            NotCompeted: {key: uuid(), value:(task)=>(task.done===false),label:'Текущие'}};

export const Priority ={ Low:{key: uuid(),value:'Low',label:'Низкий'},
                Medium:{key: uuid(),value:'Medium', label:'Средний'},
                High: {key: uuid(),value:'High',label:'Высокий'}};
