import { ThemeContext } from "../../contexts/ThemeContext";
import { useContext } from "react";
import { TaskBgColors } from "../../Constants/constants";
import PropTypes from 'prop-types';
import './Task.css'

export function Task(props) {
    const theme = useContext(ThemeContext);
    const {id, title, priority , done, changeDoneTaskParentHandler, removeTaskParentHandler} = this.props; 

    return (<div className={`Task ${theme+TaskBgColors[priority]}`}>
                    <div className='TaskHeader '>
                        <p>{title}</p>
                        <button className={'TaskBtn'} 
                        onClick={()=>RemoveTaskParentHandler(id)}>Удалить</button>
                    </div>
                    <div>
                        <div className='CompleteBlock'>
                            <label htmlFor='complete'>Выполнено:</label>
                            <input id='complete' type='checkbox' checked={props.done}
                                onChange={()=>props.parentChangeDoneTaskHandler(props.id)}/>
                            <p></p>
                        </div>
                    </div>
            </div>)
}

Task.propTypes = {
    id:PropTypes.string,
    title: PropTypes.string.isRequired,
    priority: PropTypes.string,
    done: PropTypes.bool,
    parentRemoveTaskHandler: PropTypes.func.isRequired,
    parentChangeDoneTaskHandler: PropTypes.func.isRequired
}
