import {Component} from "react";
import './Task.css'
import { bgColors } from "../../constants";
class Task extends Component{

    render(){
        const [id,title, priority,done, parentRemoveTaskHandler,changeDoneTaskParentHandler] = this.props;
        return <div className={`Task ${bgColors[priority]}`}>
                    <div className='TaskHeader '>
                        <h1>{title}</h1>
                        <div className='TaskControls'>
                            <button onClick={()=>parentRemoveTaskHandler(id)}>Удалить</button>
                        </div>
                    </div>
                    <div className='CompleteBlock'>
                        <label htmlFor='complete'>Выполнено:</label>
                        <input id='complete' 
                            type='checkbox' 
                            checked={done}
                            onChange={()=>changeDoneTaskParentHandler(id)}/>
                    </div>
              </div>
    }
}

export {Task}
