import { makeObservable,observable,action } from "mobx"
import { TaskPriority } from "../Constants/Constants"
import uuid from "react-uuid"

class ObserverableTask{
    id = uuid()
    title =''
    priority=TaskPriority.Low
    done=false

    constructor(){
        makeObservable(this,{
            title:observable,
            priority:observable,
            done:observable,
            toggleDoneState:action,
            changePriority:action,
            changeTitle:action
        })
    }

    toggleDoneState(){
        this.done = !this.done;
    }

    changePriority(priorityName){
        this.priority = TaskPriority[priorityName];
    }

    changeTitle(newTitle){
        this.title = newTitle;
    }
}

export default new ObserverableTask();
