import uuid from 'react-uuid';

class TaskStore{
    tasks = [{id:uuid(),title:'asd',done:false}]
    taskFilter = TaskFilter.All

    constructor(){
        makeObservable(this,{
            taskFilter:observable,
            tasks:observable,
            AddTask:action,
            RemoveTask:action,
            GetTasks:computed
        });
        reaction(()=>this.tasks,_=>console.log(this.tasks));
    }

    get GetTasks(){return this.tasks.filter(this.taskFilter.func);}

    AddTask=(task)=>{
        this.tasks= [...this.tasks,task];
    }

    RemoveTask=(taskId)=>{
        console.log(this);
        this.tasks = this.tasks.filter(task=>task.id !== taskId) 
    }

    ToogleStatusTask(taskId){
